<?php get_header(); ?>	<div id="banner-wrap" class="inner-banner">        <img alt="" src="<?php echo get_template_directory_uri(); ?>/images/blog-banner.jpg" />			</div></div><!--  content --><div id="content" class="content-block"><!-- finish  header --> 	<div class="centring"><!--  / left container \ --><div class="blog-left"><?php if ( is_active_sidebar( 'blog_sidebar' ) ) : ?>			<?php dynamic_sidebar( 'blog_sidebar' ); ?>	<?php endif; ?>	</div><!--  \ left container / --><div class="blog-right"><?php  wp_reset_query(); if (have_posts()) : ?>        <?php $post = $posts[0]; // Hack. Set $post so that the_date() works. ?>    <?php /* If this is a category archive */ if (is_category()) { ?>    <h1 class="pagetitle">Archive for the &#8216;<?php single_cat_title(); ?>&#8217; Category</h1>    <?php /* If this is a tag archive */ } elseif( is_tag() ) { ?>    <h1 class="pagetitle">Posts Tagged &#8216;<?php single_tag_title(); ?>&#8217;</h1>    <?php /* If this is a daily archive */ } elseif (is_day()) { ?>    <h1 class="pagetitle">Archive for <?php the_time('F jS, Y'); ?></h1>    <?php /* If this is a monthly archive */ } elseif (is_month()) { ?>    <h1 class="pagetitle">Archive for <?php the_time('F, Y'); ?></h1>    <?php /* If this is a yearly archive */ } elseif (is_year()) { ?>    <h1 class="pagetitle">Archive for <?php the_time('Y'); ?></h1>    <?php /* If this is an author archive */ } elseif (is_author()) { ?>    <h1 class="pagetitle">Author Archive</h1>    <?php /* If this is a paged archive */ } elseif (isset($_GET['paged']) && !empty($_GET['paged'])) { ?>    <h1 class="pagetitle">Blog Archives</h1>    <?php } ?>        <?php while (have_posts()) : the_post(); ?>	        <div class="blog-box" <?php // post_class() ?> id="post-<?php the_ID(); ?>">           <div class="day"> 		<h1><?php the_time('d') ?><br/><span><?php the_time('M') ?></span></h1>	   </div>	   	   <h2><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a> 	   <br/>	   <small>By:<?php the_author() ?></small>	   </h2>	   	  <a class="blog-img" href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>">	<?php the_post_thumbnail('blog-thumb'); ?></a>        <div class="entry">            <?php the_excerpt('Read the rest of this entry &raquo;'); ?>        </div>        </div>        <?php endwhile; ?>        <div class="navigation">        <div class="alignleft"><?php next_posts_link('&laquo; Older Entries') ?></div>        <div class="alignright"><?php previous_posts_link('Newer Entries &raquo;') ?></div>    </div>        <?php else : ?>        <h2 class="center">Not Found</h2>        <p class="center">Sorry, but you are looking for something that isn't here.</p>    	<?php get_search_form(); ?>        <?php endif; ?></div></div><?php get_footer(); ?>